(509, 'wpmletsubject_confirm', 'Confirm Subscription', 'yes'),
(510, 'wpmletmessage_confirm', 'Good day,\r\n\r\nThank you for subscribing to the mailing list(s): [wpmlmailinglist].\r\nPlease click the link below to activate/confirm your subscription.\r\n\r\n[wpmlactivate]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(511, 'wpmletsubject_bounce', 'Email Bounced', 'yes'),
(512, 'wpmletmessage_bounce', 'Good day,\r\n\r\nAn email has bounced.\r\nThe email address is: [wpmlemail].\r\nTotal bounces for this email/subscriber: [wpmlbouncecount].\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(513, 'wpmletsubject_unsubscribe', 'Unsubscription', 'yes'),
(514, 'wpmletmessage_unsubscribe', 'Good day Administrator,\r\n\r\nA subscriber has unsubscribed from a mailing list.\r\nThe mailing list is: [wpmlmailinglist].\r\nThe subscriber email is: [wpmlemail].\r\n\r\n[wpmlunsubscribecomments]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(515, 'wpmletsubject_unsubscribeuser', 'You are unsubscribed', 'yes'),
(516, 'wpmletmessage_unsubscribeuser', 'Your e-mail has been removed from our database.\r\nYou will no longer receive communication from us.\r\nThanks for your readership, and we hope you''ll visit us again!\r\n\r\nWas this a mistake? If it was, you can [newsletters_resubscribe]', 'yes'),
(517, 'wpmletsubject_unsubscribeconfirm', 'Confirm Unsubscribe', 'yes'),
(518, 'wpmletmessage_unsubscribeconfirm', 'Good day,\r\n\r\nPlease confirm your unsubscribe:\r\n\r\n[newsletters_unsubscribelink]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(519, 'wpmletsubject_expire', 'Subscription Expired', 'yes'),
(520, 'wpmletmessage_expire', 'Good Day,\r\n\r\nYour subscription has expired.\r\nThe mailing list is: [wpmlmailinglist].\r\nPlease click the link below to renew your subscription.\r\n\r\n[wpmlactivate]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(521, 'wpmletsubject_order', 'Paid Subscription', 'yes'),
(522, 'wpmletmessage_order', 'Good day Administrator,\r\n\r\nYou have received a paid mailing list subscription order.\r\nThe mailing list is: [wpmlmailinglist].\r\nThe subscriber email is: [wpmlemail].\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(523, 'wpmletsubject_schedule', 'Email Cron Fired', 'yes'),
(524, 'wpmletmessage_schedule', 'Good day Administrator,\r\n\r\nYour email cron has been fired as scheduled.\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(525, 'wpmletsubject_subscribe', 'New Subscription', 'yes'),
(526, 'wpmletmessage_subscribe', 'Good day Administrator,\r\n\r\nA user/visitor has just subscribed to: [wpmlmailinglist].\r\nThe email address of this subscriber is: [wpmlemail].\r\n\r\n[wpmlcustomfields]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
