(291, 0, '2017-03-13 17:56:26', '2017-03-13 20:56:26', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n	<head>\r\n		<meta name="viewport" content="width=device-width" />\r\n		<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n		<title>Politech Projetos e Consultoria</title>\r\n		\r\n		<style type="text/css">\r\n		/* -------------------------------------\r\n		    GLOBAL\r\n		    A very basic CSS reset\r\n		------------------------------------- */\r\n		* {\r\n		  margin: 0;\r\n		  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;\r\n		  box-sizing: border-box;\r\n		  /*font-size: 14px;*/\r\n		}\r\n		\r\n		img {\r\n		  max-width: 100%;\r\n		  height: auto;\r\n		}\r\n		\r\n		body {\r\n		  -webkit-font-smoothing: antialiased;\r\n		  -webkit-text-size-adjust: none;\r\n		  width: 100% !important;\r\n		  height: 100%;\r\n		  line-height: 1.6;\r\n		  font-size: 14px;\r\n		}\r\n		\r\n		/* Let''s make sure all tables have defaults */\r\n		table td {\r\n		  vertical-align: top;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    BODY & CONTAINER\r\n		------------------------------------- */\r\n		body {\r\n		  background-color: #f6f6f6;\r\n		  font-size: 14px;\r\n		  line-height: auto;\r\n		}\r\n		\r\n		.body-wrap {\r\n		  background-color: #f6f6f6;\r\n		  width: 100%;\r\n		}\r\n		\r\n		.container {\r\n		  display: block !important;\r\n		  max-width: 600px !important;\r\n		  margin: 0 auto !important;\r\n		  /* makes it centered */\r\n		  clear: both !important;\r\n		}\r\n		\r\n		.content {\r\n		  max-width: 600px;\r\n		  margin: 0 auto;\r\n		  display: block;\r\n		  padding: 20px;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    HEADER, FOOTER, MAIN\r\n		------------------------------------- */\r\n		.main {\r\n		  background-color: #fff;\r\n		  border: 1px solid #e9e9e9;\r\n		  border-radius: 3px;\r\n		}\r\n		\r\n		.content-wrap {\r\n		  padding: 20px;\r\n		}\r\n		\r\n		.content-block {\r\n		  padding: 0 0 20px;\r\n		}\r\n		\r\n		.header {\r\n		  width: 100%;\r\n		  margin-bottom: 20px;\r\n		}\r\n		\r\n		.footer {\r\n		  width: 100%;\r\n		  clear: both;\r\n		  color: #999;\r\n		  padding: 20px;\r\n		}\r\n		.footer a {\r\n		  color: #999;\r\n		}\r\n		.footer p, .footer a, .footer unsubscribe, .footer td {\r\n		  font-size: 12px;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    TYPOGRAPHY\r\n		------------------------------------- */\r\n		h1, h2, h3 {\r\n		  font-family: "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;\r\n		  color: #000;\r\n		  margin: 40px 0 20px 0;\r\n		  line-height: 1.2;\r\n		  font-weight: bold;\r\n		}\r\n		\r\n		h1 {\r\n		  font-size: 32px;\r\n		  font-weight: 500;\r\n		}\r\n		\r\n		h2 {\r\n		  font-size: 24px;\r\n		}\r\n		\r\n		h3 {\r\n		  font-size: 18px;\r\n		}\r\n		\r\n		h4 {\r\n		  font-size: 14px;\r\n		  font-weight: 600;\r\n		}\r\n		\r\n		p, ul, ol {\r\n			margin-top: 10px;\r\n		  margin-bottom: 15px;\r\n		  font-weight: normal;\r\n		}\r\n		p li, ul li, ol li {\r\n		  margin-left: 5px;\r\n		  list-style-position: inside;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    LINKS & BUTTONS\r\n		------------------------------------- */\r\n		a {\r\n		  color: #348eda;\r\n		  text-decoration: underline;\r\n		}\r\n		\r\n		.btn-primary, .newsletters_button {\r\n		  text-decoration: none;\r\n		  color: #FFF;\r\n		  background-color: #348eda;\r\n		  border: solid #348eda;\r\n		  border-width: 10px 20px;\r\n		  line-height: 2;\r\n		  font-weight: bold;\r\n		  text-align: center;\r\n		  cursor: pointer;\r\n		  display: inline-block;\r\n		  border-radius: 5px;\r\n		  text-transform: capitalize;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    OTHER STYLES THAT MIGHT BE USEFUL\r\n		------------------------------------- */\r\n		.last {\r\n		  margin-bottom: 0;\r\n		}\r\n		\r\n		.first {\r\n		  margin-top: 0;\r\n		}\r\n		\r\n		.aligncenter {\r\n		  text-align: center;\r\n		}\r\n		\r\n		.alignright {\r\n		  text-align: right;\r\n		}\r\n		\r\n		.alignleft {\r\n		  text-align: left;\r\n		}\r\n		\r\n		.clear {\r\n		  clear: both;\r\n		}\r\n		\r\n		hr {\r\n		    border: 0;\r\n		    height: 0;\r\n		    border-top: 1px solid rgba(0, 0, 0, 0.1);\r\n		    border-bottom: 1px solid rgba(255, 255, 255, 0.3);\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    ALERTS\r\n		    Change the class depending on warning email, good email or bad email\r\n		------------------------------------- */\r\n		.alert {\r\n		  font-size: 16px;\r\n		  color: #fff;\r\n		  font-weight: 500;\r\n		  padding: 20px;\r\n		  text-align: center;\r\n		  border-radius: 3px 3px 0 0;\r\n		}\r\n		.alert a {\r\n		  color: #fff;\r\n		  text-decoration: none;\r\n		  font-weight: 500;\r\n		  font-size: 16px;\r\n		}\r\n		.alert.alert-warning {\r\n		  background-color: #FF9F00;\r\n		}\r\n		.alert.alert-bad {\r\n		  background-color: #D0021B;\r\n		}\r\n		.alert.alert-good {\r\n		  background-color: #68B90F;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    INVOICE\r\n		    Styles for the billing table\r\n		------------------------------------- */\r\n		.invoice {\r\n		  margin: 40px auto;\r\n		  text-align: left;\r\n		  width: 80%;\r\n		}\r\n		.invoice td {\r\n		  padding: 5px 0;\r\n		}\r\n		.invoice .invoice-items {\r\n		  width: 100%;\r\n		}\r\n		.invoice .invoice-items td {\r\n		  border-top: #eee 1px solid;\r\n		}\r\n		.invoice .invoice-items .total td {\r\n		  border-top: 2px solid #333;\r\n		  border-bottom: 2px solid #333;\r\n		  font-weight: 700;\r\n		}\r\n		\r\n		/* -------------------------------------\r\n		    RESPONSIVE AND MOBILE FRIENDLY STYLES\r\n		------------------------------------- */\r\n		@media only screen and (max-width: 640px) {\r\n		  h1, h2, h3, h4 {\r\n		    font-weight: 600 !important;\r\n		    margin: 20px 0 5px !import